import React from "react";
import dosyaEkleIcon from "../son_pictures/dosyaEkle.png";
import kimlikIcon from "../son_pictures/kimlik.png";

function YorumYap({
  fileInputRef,
  setYaziYazAcik,
  comboGorunur,
  setComboGorunur,
  hesapGorunurluk,
  setHesapGorunurluk,
}) {
  return (
    <>
      {/* Sayfanın geri kalanını karartan arka plan */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          backgroundColor: "rgba(0, 0, 0, 0.4)",
          zIndex: 999,
        }}
        onClick={() => setYaziYazAcik(false)} // boş alana tıklayınca kapanır
      ></div>

      {/* Yorum modal kutusu */}
      <div
        className="form-kutusu"
        style={{
          backgroundColor: "#ff7c5c",
          backgroundSize: "100% 100%",
          backgroundRepeat: "no-repeat",
          backgroundPosition: "center",
          color: "#1b475d",
          padding: "20px",
          borderRadius: "8px",
          position: "fixed",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          zIndex: 1000,
          width: "400px",
          boxShadow: "0 0 15px rgba(0,0,0,0.3)",
        }}
      >
        <label>Başlık</label>
        <input
          type="text"
          placeholder="Yazınızın başlığı"
          style={{
            width: "90%",
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        />
        <label>İçerik</label>
        <textarea
          placeholder="Buraya yazınızı yazabilirsiniz..."
          rows="5"
          style={{
            width: "90%",
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        />
        <div
          style={{
            marginTop: "10px",
            display: "flex",
            alignItems: "center",
            gap: "10px",
          }}
        >
          <img
            src={dosyaEkleIcon}
            alt="Dosya Ekle"
            style={{ width: "24px", height: "24px", cursor: "pointer" }}
            onClick={() => fileInputRef.current.click()}
          />
          <span style={{ fontSize: "10px", color: "#1b475d" }}>
            *Yazın herhangi bir kitleyi hedef almamalı ve küfür içermemeli, aksi
            halde wall’dan kaldırılacağını unutma!
          </span>
          <input
            type="file"
            style={{ display: "none" }}
            ref={fileInputRef}
            onChange={(e) => {
              const selectedFile = e.target.files[0];
              if (selectedFile) {
                alert(`Seçilen dosya: ${selectedFile.name}`);
              }
            }}
          />
        </div>

        <div
          style={{
            display: "flex",
            alignItems: "center",
            gap: "10px",
            marginTop: "10px",
          }}
        >
          <img
            src={kimlikIcon}
            alt="Hesap Ayarı"
            style={{ width: "24px", height: "24px", cursor: "pointer" }}
            onClick={() => setComboGorunur(!comboGorunur)}
          />

          {comboGorunur && (
            <select
              value={hesapGorunurluk}
              onChange={(e) => {
                setHesapGorunurluk(e.target.value);
                setComboGorunur(false);
              }}
              style={{
                padding: "4px",
                borderRadius: "4px",
                backgroundColor: "#fff",
                border: "1px solid #ccc",
              }}
            >
              <option value="açık">Hesap Adı Açık</option>
              <option value="gizli">Hesap Adı Gizli</option>
            </select>
          )}

          <p style={{ fontSize: "12px", color: "#444", margin: 0 }}>
            Seçilen Görünürlük:{" "}
            {hesapGorunurluk === "açık" ? "Herkese açık" : "Gizli"}
          </p>
        </div>

        <div className="buton-grubu" style={{ marginTop: "20px" }}>
          <button
            onClick={() => setYaziYazAcik(false)}
            style={{
              marginRight: "10px",
              padding: "8px 16px",
              borderRadius: "4px",
              backgroundColor: "#1b475d",
              color: "white",
              border: "none",
              cursor: "pointer",
            }}
          >
            Paylaş
          </button>
          <button
            onClick={() => setYaziYazAcik(false)}
            style={{
              padding: "8px 16px",
              borderRadius: "4px",
              backgroundColor: "#ccc",
              border: "none",
              cursor: "pointer",
            }}
          >
            Kapat
          </button>
        </div>
      </div>
    </>
  );
}

export default YorumYap;
