import React, { useState, useEffect } from "react";
import zamanLogo from "../son_pictures/thumbnail_zaman_logo.png";
import filtreLogo from "../son_pictures/thumbnail_filtre_logo.png";
import kullaniciLogo from "../son_pictures/kullanici_logo.png";

const MainContent = () => {
  const [yorumlar, setYorumlar] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/yorumlar")
      .then((res) => res.json())
      .then((data) => {
        const yorumlarWithLikes = data.map((yorum) => ({
          ...yorum,
          liked: false,
          likes: 0,
        }));
        setYorumlar(yorumlarWithLikes);
      })
      .catch((err) => console.error(err));
  }, []);

  const toggleLike = (id) => {
    setYorumlar((prev) =>
      prev.map((yorum) =>
        yorum.id === id
          ? {
              ...yorum,
              liked: !yorum.liked,
              likes: yorum.liked ? yorum.likes - 1 : yorum.likes + 1,
            }
          : yorum
      )
    );
  };

  return (
    <div className="flex-grow" style={{ height: "80vh" }}>
      {/* Filtreleme k覺sm覺 - scroll d覺覺, dropdown'lar burada */}
      <div className="mb-4">
        <div className="flex gap-3 mr-24 relative z-50">
          {/* Zaman filtre */}
          <div className="relative group">
            <a href="#" className="cursor-pointer">
              <img src={zamanLogo} alt="Zaman" className="w-12 h-12" />
            </a>
            <div className="absolute left-0 hidden group-hover:block space-y-2 bg-white text-black rounded-lg shadow-lg w-48 border-4 border-[#ff7c5c] z-50">
              <a href="#" className="block px-4 py-2 hover:bg-[#ff7c5c] hover:text-white rounded">
                Son 24 Saat
              </a>
              <a href="#" className="block px-4 py-2 hover:bg-[#ff7c5c] hover:text-white rounded">
                Son 1 hafta
              </a>
              <a href="#" className="block px-4 py-2 hover:bg-[#ff7c5c] hover:text-white rounded">
                Son 1 Ay
              </a>
            </div>
          </div>

          {/* Beeni filtre */}
          <div className="relative group">
            <a href="#" className="cursor-pointer">
              <img src={filtreLogo} alt="Filtre" className="w-12 h-12" />
            </a>
            <div className="absolute left-0 hidden group-hover:block space-y-2 bg-white text-black rounded-lg shadow-lg w-48 border-4 border-[#ff7c5c] z-50">
              <a href="#" className="block px-4 py-2 hover:bg-[#ff7c5c] hover:text-white rounded">
                Varsay覺lan
              </a>
              <a href="#" className="block px-4 py-2 hover:bg-[#ff7c5c] hover:text-white rounded">
                Beeni Say覺s覺(fazla)
              </a>
              <a href="#" className="block px-4 py-2 hover:bg-[#ff7c5c] hover:text-white rounded">
                Beeni Say覺s覺(az)
              </a>
            </div>
          </div>
        </div>
      </div>

      {/* Yorumlar k覺sm覺 - scroll burada */}
      <div
        className="overflow-y-auto"
        style={{ height: "calc(80vh - 72px)" /* filtre alan y羹kseklii 癟覺kar覺ld覺 */ }}
      >
        {yorumlar.map((yorum) => (
          <div
            key={yorum.id}
            className="relative border p-4 mb-4 rounded shadow"
          >
            <h4 className="font-semibold text-lg mb-2">
              {yorum.title || "Bal覺ks覺z Yorum"}
            </h4>

            <p>{yorum.content}</p>

            {yorum.image_url && (
              <img
                src={`http://localhost:5000/${yorum.image_url}`}
                alt="Yorum resmi"
                className="w-full max-w-xs mt-2"
              />
            )}

            <div className="flex items-center text-xs mt-2 text-gray-600">
              <span className="flex items-center gap-2">
                <img
                  src={kullaniciLogo}
                  alt="Kullan覺c覺"
                  className="w-8 h-8 rounded-full"
                />
                {yorum.user_name || "Bilinmeyen Kullan覺c覺"}
              </span>
              <span className="ml-2">
                {new Date(yorum.created_at).toLocaleString()}
              </span>
            </div>

            {/* Yan覺t Ver ve Kalp */}
            <div className="absolute bottom-2 right-4 flex items-center gap-3">
              {/* Yan覺t Ver Butonu */}
              <button
                className="text-white text-sm px-3 py-1 rounded-full transition duration-300"
                style={{ backgroundColor: "#ff7c5c" }}
              >
                Yan覺t Ver
              </button>

              {/* Kalp ve Say覺 */}
              <div className="flex items-center gap-1">
                <span
                  onClick={() => toggleLike(yorum.id)}
                  className={`text-2xl cursor-pointer transition-colors duration-300 ${
                    yorum.liked ? "text-red-500" : "text-gray-400"
                  } hover:scale-110 select-none`}
                >
                  {yorum.liked ? "歹" : ""}
                </span>
                <span className="text-sm text-gray-700">{yorum.likes}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MainContent;
