import React, { useState, useEffect } from "react";
import kullaniciLogo from "../son_pictures/kullanici_logo.png"; // Kullanıcı logosu

const MainContent = () => {
  const [yorumlar, setYorumlar] = useState([]);

  useEffect(() => {
    // Sayfa yüklendiğinde yorumları çek
    fetch("http://localhost:5000/api/yorumlar")
      .then((res) => res.json())
      .then((data) => setYorumlar(data))
      .catch((err) => console.error(err));
  }, []);

  // Yeni yorum eklendiğinde listeye ekle
  const handleYorumEklendi = (yeniYorum) => {
    setYorumlar((oncekiYorumlar) => [yeniYorum, ...oncekiYorumlar]);
  };

  return (
    <div className="flex-grow">
      {/* Buraya YorumYap bileşenini ekle ve onYorumEklendi propsunu ver */}
      {/* <YorumYap onYorumEklendi={handleYorumEklendi} /> */}

      {/* Yorum listesini render et */}
      {yorumlar.map((yorum) => (
        <div key={yorum.id} className="border p-4 mb-4 rounded shadow">
          <p>{yorum.content}</p>
          {yorum.image_url && (
            <img
              src={`http://localhost:5000/${yorum.image_url}`}
              alt="Yorum resmi"
              className="w-full max-w-xs mt-2"
            />
          )}
          <div className="flex items-center text-xs mt-2 text-gray-600">
            <span className="flex items-center gap-2">
              <img src={kullaniciLogo} alt="Kullanıcı" className="w-8 h-8" />
              {yorum.user_name || "Bilinmeyen Kullanıcı"}
            </span>
            <span className="ml-2">{new Date(yorum.created_at).toLocaleString()}</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default MainContent;
