import React from "react";
import { Link, useNavigate } from "react-router-dom";
import logo from "../son_pictures/logo.png";
import bildirim from "../son_pictures/bildirim_logo.png";
import kullanici from "../son_pictures/kullanici_logo.png";
import mesaj from "../son_pictures/mesajkutusu_logo.png";
import ucNokta from "../son_pictures/uc_nokta_logo.png";

const Header = ({ user, setUser }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    setUser(null); // Kullanıcı state'ini temizle
    localStorage.removeItem('user'); // localStorage temizle
    navigate('/giris'); // Giriş sayfasına yönlendir
  };

  return (
    <div className="flex items-center justify-between p-4 border-b">
      <div className="flex items-center gap-2">
        <Link to="/anasayfa">
          <img src={logo} alt="Logo" className="h-16 w-auto" />
        </Link>
        <span className="text-sm italic"></span>
      </div>

      <div className="flex items-center gap-4">
        <Link to="/anasayfa" className="px-4 py-2 border rounded hover:bg-gray-200">Anasayfa</Link>
        <Link to="/hakkimizda" className="px-4 py-2 border rounded hover:bg-gray-200">Hakkımızda</Link>
        <Link to="/bolumler" className="px-4 py-2 border rounded hover:bg-gray-200">Bölümler</Link>

        {user ? (
          <div className="relative group">
            <button className="flex items-center gap-1">
              <img src={kullanici} alt="Kullanıcı" className="h-8 w-8 rounded-full" />
              {user.name}
              <img src={ucNokta} alt="Ayarlar" className="h-4 w-4" />
            </button>
            <div className="absolute right-0 mt-2 w-40 bg-white border rounded shadow-lg opacity-0 group-hover:opacity-100 transition-opacity">
              <Link to="/profil" className="block px-4 py-2 hover:bg-gray-200">Profil</Link>
              <Link to="/bildirimler" className="block px-4 py-2 hover:bg-gray-200">Bildirimler</Link>
              <Link to="/mesajlar" className="block px-4 py-2 hover:bg-gray-200">Mesajlar</Link>
              <a href="#" onClick={e => { e.preventDefault(); handleLogout(); }} className="block px-4 py-2 hover:bg-gray-200">Çıkış yap</a>
            </div>
          </div>
        ) : (
          <Link to="/giris" className="px-4 py-2 border rounded hover:bg-gray-200">Giriş Yap</Link>
        )}
      </div>
    </div>
  );
};

export default Header;
