import React, { useState, useEffect, useRef } from "react";
import zamanLogo from "../son_pictures/thumbnail_zaman_logo.png";
import filtreLogo from "../son_pictures/thumbnail_filtre_logo.png";
import kullaniciLogo from "../son_pictures/kullanici_logo.png";
import YorumYapSade from "./YorumYapSade";

const MainContent = () => {
  const [yorumlar, setYorumlar] = useState([]);
  const [yanitlaAcik, setYanitlaAcik] = useState(false);
  const fileInputRef = useRef(null);
  const [comboGorunur, setComboGorunur] = useState(false);
  const [hesapGorunurluk, setHesapGorunurluk] = useState("açık");

  useEffect(() => {
    fetch("http://localhost:5000/api/yorumlar")
      .then((res) => res.json())
      .then((data) => {
        const yorumlarWithLikes = data.map((yorum) => ({
          ...yorum,
          liked: false,
          likes: 0,
        }));
        setYorumlar(yorumlarWithLikes);
      })
      .catch((err) => console.error(err));
  }, []);

  const toggleLike = (id) => {
    setYorumlar((prev) =>
      prev.map((yorum) =>
        yorum.id === id
          ? {
              ...yorum,
              liked: !yorum.liked,
              likes: yorum.liked ? yorum.likes - 1 : yorum.likes + 1,
            }
          : yorum
      )
    );
  };

  const handleYeniYorum = (yeniYorum) => {
    setYorumlar((prev) => [
      {
        ...yeniYorum,
        liked: false,
        likes: 0,
      },
      ...prev,
    ]);
  };

  return (
    <div className="flex-grow" style={{ height: "80vh", overflowY: "auto" }}>
      <div className="flex justify-between items-center px-4 py-3 bg-white shadow rounded mb-4">
        <div className="flex items-center gap-3">
          <button className="flex items-center gap-1 px-3 py-1 rounded-full bg-[#1b475d] text-white text-sm hover:bg-[#163a4d]">
            <img src={zamanLogo} alt="Zaman" className="w-5 h-5" />
            En Yeni
          </button>
          <button className="flex items-center gap-1 px-3 py-1 rounded-full bg-[#1b475d] text-white text-sm hover:bg-[#163a4d]">
            <img src={filtreLogo} alt="Filtre" className="w-5 h-5" />
            Filtrele
          </button>
        </div>
      </div>

      {yorumlar.map((yorum) => (
        <div
          key={yorum.id}
          className="bg-white shadow rounded p-4 mb-4 mx-4 relative"
        >
          <div className="flex items-center mb-2">
            <img
              src={kullaniciLogo}
              alt="Kullanıcı"
              className="w-8 h-8 rounded-full mr-2"
            />
            <span className="font-semibold text-sm">
              {yorum.username || "Anonim"}
            </span>
          </div>
          <p className="text-sm mb-2">{yorum.content}</p>

          {yorum.image && (
            <img
              src={`http://localhost:5000/uploads/${yorum.image}`}
              alt="Yorum görseli"
              className="mt-2 rounded"
              style={{ maxWidth: "100%" }}
            />
          )}

          <div className="flex items-center text-sm mt-2 space-x-4">
            <button
              onClick={() => toggleLike(yorum.id)}
              className="text-[#1b475d] hover:underline"
            >
              {yorum.liked ? "Beğendin" : "Beğen"} ({yorum.likes})
            </button>
            <button
              onClick={() => setYanitlaAcik(true)}
              className="text-gray-500 hover:underline"
            >
              Yanıt Ver
            </button>
          </div>
        </div>
      ))}

      {yanitlaAcik && (
        <YorumYapSade
          fileInputRef={fileInputRef}
          setYaziYazAcik={setYanitlaAcik}
          comboGorunur={comboGorunur}
          setComboGorunur={setComboGorunur}
          hesapGorunurluk={hesapGorunurluk}
          setHesapGorunurluk={setHesapGorunurluk}
          onYorumEklendi={handleYeniYorum}
        />
      )}
    </div>
  );
};

export default MainContent;
