import React, { useState, useEffect } from "react";
import zamanLogo from "../son_pictures/thumbnail_zaman_logo.png";
import filtreLogo from "../son_pictures/thumbnail_filtre_logo.png";
import kullaniciLogo from "../son_pictures/kullanici_logo.png";
import YorumYapSade from "./YorumYapSade";

const MainContent = () => {
  const [yorumlar, setYorumlar] = useState([]);
  const [yanitlaAcik, setYanitlaAcik] = useState(false);
  const [fileInputRef] = useState(React.createRef());
  const [comboGorunur, setComboGorunur] = useState(false);
  const [hesapGorunurluk, setHesapGorunurluk] = useState("açık");

  useEffect(() => {
    fetch("http://localhost:5000/api/yorumlar")
      .then((res) => res.json())
      .then((data) => {
        const yorumlarWithLikes = data.map((yorum) => ({
          ...yorum,
          liked: false,
          likes: 0,
        }));
        setYorumlar(yorumlarWithLikes);
      })
      .catch((err) => console.error(err));
  }, []);

  const toggleLike = (id) => {
    setYorumlar((prev) =>
      prev.map((yorum) =>
        yorum.id === id
          ? {
              ...yorum,
              liked: !yorum.liked,
              likes: yorum.liked ? yorum.likes - 1 : yorum.likes + 1,
            }
          : yorum
      )
    );
  };

  return (
    <div className="flex-grow" style={{ height: "80vh", overflowY: "auto" }}>
      <div className="flex justify-between items-center px-4 py-2 bg-white shadow rounded mb-4">
        <div className="flex items-center space-x-4">
          <button className="flex items-center px-3 py-1 rounded-full bg-gray-100 hover:bg-gray-200 text-sm">
            <img src={zamanLogo} alt="Zaman" className="w-4 h-4 mr-2" />
            En Yeni
          </button>
          <button className="flex items-center px-3 py-1 rounded-full bg-gray-100 hover:bg-gray-200 text-sm">
            <img src={filtreLogo} alt="Filtre" className="w-4 h-4 mr-2" />
            Filtrele
          </button>
        </div>
      </div>

      {yorumlar.map((yorum) => (
        <div key={yorum.id} className="relative border p-4 mb-4 rounded shadow">
          <h4 className="font-semibold text-lg mb-2">
            {yorum.title || "Başlıksız Yorum"}
          </h4>
          <p>{yorum.content}</p>

          {yorum.image_url && (
            <img
              src={`http://localhost:5000/${yorum.image_url}`}
              alt="Yorum resmi"
              className="w-full max-w-xs mt-2"
            />
          )}

          <div className="flex items-center text-xs mt-2 text-gray-600">
            <span className="flex items-center gap-2">
              <img
                src={kullaniciLogo}
                alt="Kullanıcı"
                className="w-8 h-8 rounded-full"
              />
              {yorum.user_name || "Bilinmeyen Kullanıcı"}
            </span>
            <span className="ml-2">
              {new Date(yorum.created_at).toLocaleString()}
            </span>
          </div>

          <div className="absolute bottom-2 right-4 flex items-center gap-3">
            <button
              onClick={() => setYanitlaAcik(true)}
              className="text-white text-sm px-3 py-1 rounded-full transition duration-300"
              style={{ backgroundColor: "#ff7c5c" }}
            >
              Yanıt Ver
            </button>

            <div className="flex items-center gap-1">
              <span
                onClick={() => toggleLike(yorum.id)}
                className={`text-2xl cursor-pointer transition-colors duration-300 ${
                  yorum.liked ? "text-red-500" : "text-gray-400"
                } hover:scale-110 select-none`}
              >
                {yorum.liked ? "❤️" : "🤍"}
              </span>
              <span className="text-sm text-gray-700">{yorum.likes}</span>
            </div>
          </div>
        </div>
      ))}

      {yanitlaAcik && (
        <YorumYapSade
          fileInputRef={fileInputRef}
          setYaziYazAcik={setYanitlaAcik}
          comboGorunur={comboGorunur}
          setComboGorunur={setComboGorunur}
          hesapGorunurluk={hesapGorunurluk}
          setHesapGorunurluk={setHesapGorunurluk}
          onYorumEklendi={(yeniYorum) =>
            setYorumlar((prev) => [yeniYorum, ...prev])
          }
        />
      )}
    </div>
  );
};

export default MainContent;
